const slider=document.querySelector(".popular-products .slider"),products=slider.querySelectorAll(".catalog-item"),buttonPrev=slider.querySelector(".slider-button-prev"),buttonNext=slider.querySelector(".slider-button-next");let productsTotal=products.length,currentSlideNumber=1;const toggleHideSlide=function(){products[currentSlideNumber-1].classList.toggle("hidden")};buttonPrev.addEventListener("click",function(){toggleHideSlide(),1===currentSlideNumber?currentSlideNumber=productsTotal:currentSlideNumber--,toggleHideSlide()}),buttonNext.addEventListener("click",function(){toggleHideSlide(),currentSlideNumber===productsTotal?currentSlideNumber=1:currentSlideNumber++,toggleHideSlide()});
